
public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5;
		if(x!=5)
			System.out.println("value of x is :"+x);
		System.out.println("the value of x is 5");
		//if we use if statement we need to give {} for the printing statements when we use {} the alone line will be printed 
//if we dont have {} then the second print line will be a scope
	}

}
